import unittest
import itertools as it

def calc(input, size):
    for i in range(len(input)):
        if len(set(input[i:i+size])) == size:
            return i + size

def day1(input):
    return calc(input, 4)

def day2(input):
    return calc(input, 14)


class TestPart1(unittest.TestCase):
    def test_sample(self):
        self.assertEqual(7, day1('mjqjpqmgbljsphdztnvjfqwrcgsmlb'))
        self.assertEqual(5, day1('bvwbjplbgvbhsrlpgdmjqwftvncz'))
        self.assertEqual(6, day1('nppdvjthqldpwncqszvftbrmjlhg'))
        self.assertEqual(10, day1('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg'))
        self.assertEqual(11, day1('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'))

    def test_input(self):
        self.assertEqual(1531, day1(''))


class TestPart2(unittest.TestCase):
    def test_sample(self):
        self.assertEqual(19, day2('mjqjpqmgbljsphdztnvjfqwrcgsmlb'))
        self.assertEqual(23, day2('bvwbjplbgvbhsrlpgdmjqwftvncz'))
        self.assertEqual(23, day2('nppdvjthqldpwncqszvftbrmjlhg'))
        self.assertEqual(29, day2('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg'))
        self.assertEqual(26, day2('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'))

    def test_input(self):
        self.assertEqual(2518, day2(''))
